mapper.select:
Select y.ValAd, f.ID, y.YhName, y.XqName, y.BuildNO, y.CellNO, y.HouseNO, y.Bjxx, y.BjTime, y.Bz, f.ValAd, f.Status, f.FamKd, f.LockSt, f.JFSt, f.Tqyb, y.YLFQ, f.ValTemp, f.RoomTemp, f.QgID, y.SFJF, y.SFQF, y.SFTR, y.YHBM, y.JFSJ, f.RecordTime, y.HeatArea, y.Yhfl, y.YhRb, y.Cjq, y.IDNum, y.WCAd from T_FmInfo f,T_YhInfo y where y.ValAd = f.ValAd and y.SFJF='是'
and y.XqName=#{xqName}
and y.BuildNo=#{buildNO}
and y.CellNO=#{cellNO}
ORDER BY y.HouseNO asc

		select y.id ,y.YhName,y.ValAd,y.yhfl,f.QgID,y.XqName,y.BuildNO,y.CellNO,y.HouseNO,f.Status,y.HeatArea,
		f.RoomTemp,f.LockSt,y.IDNum,f.FamKd,f.ValTemp,f.Tqyb,y.SFJF,y.WCAd ,y.Bz,CONVERT(varchar(100),f.RecordTime , 20)as RecordTime,
		f.MTemSet,f.LTemSet
		from T_YhInfo y ,T_FmInfo f where y.ValAd=f.ValAd and (y.yhrb=0 or y.yhrb=2)

        and y.XqName=#{xqm}


        and y.BuildNO=#{ldh}


	    and y.CellNO=#{dyh}


	    and y.HouseNO=#{hh}


	    and y.yhfl=#{yhfl}



	    and  f.FamKd >= #{FamKd1}


	    and  f.FamKd <= #{FamKd2}



	    and  f.RoomTemp >= #{RoomTemp1}


	    and  f.RoomTemp <= #{RoomTemp2}



	    and  f.ValTemp >= #{ValTemp1}


	    and  f.ValTemp <= #{ValTemp2}

mapper.update:
update T_FmInfo set
ValAd=#{ValAd},QgID=#{QgID}		
where ValAd=#{ValAd}

mapper.select:
		select * ,CONVERT(varchar(100), f.RecordTime , 20)as time  from T_FmHistory19  f, T_YhInfo y where y.ValAd=f.ValAd


        and y.XqName=#{xqm}


        and y.BuildNO=#{ldh}


	    and y.CellNO=#{dyh}


	    and y.HouseNO=#{hh}

		order by   RecordTime desc

		select* from (select top 120 FamKd,Tqyb,CONVERT(varchar(100), f.RecordTime , 20)as time,RoomTemp,ValTemp  from T_FmHistory19  f, T_YhInfo y where y.ValAd=f.ValAd


        and y.XqName=#{xqm}


        and y.BuildNO=#{ldh}


	    and y.CellNO=#{dyh}


	    and y.HouseNO=#{hh}


		order by   RecordTime desc) as t order by t.time asc

		select * ,CONVERT(varchar(100),TJ_Time , 20)as slsj,CONVERT(varchar(100),WC_Time , 20)as wxsj from T_KfInfo  k  where 1=1


        and k.XqName=#{xqm}


        and k.BuildNO=#{ldh}


	    and k.CellNO=#{dyh}


	    and k.SH=#{hh}

		order by TJ_Time desc

		 select  * ,CONVERT(varchar(100),Date , 20)as time from TjfxKd where 1=1


        and XqName=#{xqm}


	    and date >= #{startTime}


	     and date <=#{endTime}

		order by   date asc

	select  * ,CONVERT(varchar(100),Date , 20)as time from XqAvg  where 1=1

			and XqName=#{xqm}


	    and date >= #{startTime}


	     and date <=#{endTime}

        order by date asc

		select y.id ,y.YhName,y.ValAd,y.yhfl,f.QgID,y.XqName,y.BuildNO,y.CellNO,y.HouseNO,f.Status,y.HeatArea,
		f.RoomTemp,f.LockSt,y.IDNum,f.FamKd,f.ValTemp,f.Tqyb,y.SFJF,y.WCAd ,y.Bz,CONVERT(varchar(100),f.RecordTime , 20)as RecordTime
		from T_YhInfo y ,T_FmInfo f where y.ValAd=f.ValAd and (y.yhrb=0 or y.yhrb=2)

        and y.XqName=#{xqm}


        and y.BuildNO=#{ldh}


	    and y.CellNO=#{dyh}


	    and y.HouseNO LIKE #{hh}+'%' and len(y.HouseNO)-len(#{hh})=2

select REPLACE (REPLACE (y.XqName, '（智慧供热区域）',''), '（智慧供热）','')  
  as xqm,y.BuildNO as ldh,y.CellNO as dyh,y.HouseNO as hh

from T_YhInfo y  where  (y.yhrb=0 or y.yhrb=2)
